// A single tile on the game map
class Tile {
  x: number;
  y: number;
  type: number;
  explored: boolean;
  visible: boolean;

  constructor(x: number, y: number, tileType: number) {
    this.x = x;
    this.y = y;
    this.type = tileType; // 0 = wall, 1 = floor, 2 = door
    this.explored = false;
    this.visible = false;
  }
}

// A rectangular room within the dungeon
class Room {
  x: number;
  y: number;
  w: number;
  h: number;
  center: [number, number];

  constructor(x: number, y: number, w: number, h: number) {
    this.x = x;
    this.y = y;
    this.w = w;
    this.h = h;
    this.center = [x + Math.floor(w / 2), y + Math.floor(h / 2)];
  }

  intersects(other: Room): boolean {
    return (
      this.x < other.x + other.w &&
      this.x + this.w > other.x &&
      this.y < other.y + other.h &&
      this.y + this.h > other.y
    );
  }
}

// Represents any character or object in the game world
class Entity {
  x: number;
  y: number;
  char: string;
  color: string;
  name: string;
  max_hp: number;
  hp: number;
  attack: number;
  defense: number;
  exp: number;
  alive: boolean;
  sprite: HTMLImageElement | null;
  show_health: boolean;
  health_bar_time: number;
  special: Record<string, any>;

  constructor(
    x: number,
    y: number,
    char: string,
    color: string,
    name: string,
    hp = 1,
    attack = 0,
    defense = 0,
    exp = 0
  ) {
    this.x = x;
    this.y = y;
    this.char = char;
    this.color = color;
    this.name = name;
    this.max_hp = hp;
    this.hp = hp;
    this.attack = attack;
    this.defense = defense;
    this.exp = exp;
    this.alive = true;
    this.sprite = null;
    this.show_health = false;
    this.health_bar_time = 0;
    this.special = {};
    this.load_sprite();
  }

  load_sprite() {
    const spriteMapping: Record<string, string> = {
      "Player": PLAYER_IMAGE,
      "goblin": GOBLIN_IMAGE,
      "orc": ORC_IMAGE,
      "zombie": ZOMBIE_IMAGE,
      "troll": TROLL_IMAGE,
      "ghost": GHOST_IMAGE,
      "Health Potion": HEALTH_POTION_IMAGE,
      "Weapon": WEAPON_IMAGE,
      "Armor": ARMOR_IMAGE,
      "Treasure": TREASURE_IMAGE,
      "Gold": GOLD_IMAGE,
      "Exit": EXIT_IMAGE
    };

    if (spriteMapping[this.name]) {
      this.sprite = new Image();
      this.sprite.src = spriteMapping[this.name];
    }
  }

  draw_health_bar(ctx: CanvasRenderingContext2D, x: number, y: number, width: number, height: number) {
    const healthRatio = this.hp / this.max_hp;
    const barWidth = width;
    const barHeight = Math.max(4, height / 8);

    // Outline
    ctx.fillStyle = BLACK;
    ctx.fillRect(x - 1, y - barHeight - 1, barWidth + 2, barHeight + 2);
    
    // Background (missing health)
    ctx.fillStyle = RED;
    ctx.fillRect(x, y - barHeight, barWidth, barHeight);
    
    // Foreground (current health)
    ctx.fillStyle = GREEN;
    ctx.fillRect(x, y - barHeight, barWidth * healthRatio, barHeight);
  }

  draw(ctx: CanvasRenderingContext2D, x: number, y: number, size: number) {
    if (this.sprite && this.sprite.complete) {
      ctx.drawImage(this.sprite, x, y, size, size);
    } else {
      // Fallback to character representation
      ctx.fillStyle = this.color;
      ctx.font = `${size}px Arial`;
      ctx.fillText(this.char, x + size / 4, y + size - size / 4);
    }
    
    // Draw health bar if needed
    if (this.name !== "Player" && this.alive && 
        (this.show_health || Date.now() - this.health_bar_time < 2000)) {
      this.draw_health_bar(ctx, x, y, size, size);
      this.show_health = false;
    }
  }
}

// Represents the player character
class Player extends Entity {
  level: number;
  exp: number;
  next_level: number;
  vision_radius: number;
  crit_chance: number;
  crit_multiplier: number;
  gold: number;
  poisoned: boolean;
  poison_damage: number;
  poison_duration: number;
  last_direction: string;
  left_sprite: HTMLImageElement | null;
  right_sprite: HTMLImageElement | null;

  constructor(x: number, y: number) {
    super(x, y, "@", GREEN, "Player", 100, 8, 5);
    this.level = 1;
    this.exp = 0;
    this.next_level = 100;
    this.vision_radius = 6;
    this.crit_chance = 0.1;
    this.crit_multiplier = 1.5;
    this.gold = 0;
    this.poisoned = false;
    this.poison_damage = 0;
    this.poison_duration = 0;
    this.last_direction = "right";
    this.left_sprite = null;
    this.right_sprite = null;
    this.load_sprites();
  }

  level_up(): string {
    this.level += 1;
    this.max_hp += 5 + this.level;
    this.hp = this.max_hp;
    this.attack += 2 + Math.floor(this.level / 2);
    this.defense += 2 + Math.floor(this.level / 3);
    this.next_level += 100 * this.level;
    this.crit_chance = Math.min(0.3, this.crit_chance + 0.02);
    return `Level up! You are now level ${this.level}!`;
  }

  load_sprites() {
    if (PLAYER_IMAGE) {
      this.right_sprite = new Image();
      this.right_sprite.src = PLAYER_IMAGE;
      
      // Create left-facing sprite by flipping (would need canvas manipulation)
      this.left_sprite = new Image();
      this.left_sprite.src = PLAYER_IMAGE; // In a real app, you'd flip this
    }
  }
}

// Main game class
class Game {
  map_width: number;
  map_height: number;
  tiles: Tile[][];
  player: Player;
  entities: Entity[];
  items: Entity[];
  exit_pos: [number, number];
  message: string;
  message_time: number;
  camera_x: number;
  camera_y: number;
  game_state: string;
  dungeon_level: number;
  combat_log: string[];
  max_log_entries: number;
  wall_color: string;
  floor_color: string;
  enemy_agro_range: number;

  constructor() {
    this.map_width = 100;
    this.map_height = 100;
    this.tiles = [];
    this.player = new Player(0, 0);
    this.entities = [];
    this.items = [];
    this.exit_pos = [0, 0];
    this.message = "";
    this.message_time = 0;
    this.camera_x = 0;
    this.camera_y = 0;
    this.game_state = "playing";
    this.dungeon_level = 1;
    this.combat_log = [];
    this.max_log_entries = 10;
    this.wall_color = STONE;
    this.floor_color = DARK_GRAY;
    this.enemy_agro_range = 1;
    this.generate_dungeon();
  }

  generate_dungeon() {
    // Choose random colors for this level
    this.wall_color = WALL_PALETTE[Math.floor(Math.random() * WALL_PALETTE.length)];
    this.floor_color = FLOOR_PALETTE[Math.floor(Math.random() * FLOOR_PALETTE.length)];
    
    // Ensure wall and floor colors have enough contrast
    while (this.colorDistance(this.wall_color, this.floor_color) < 60) {
      this.floor_color = FLOOR_PALETTE[Math.floor(Math.random() * FLOOR_PALETTE.length)];
    }
    
    // Reset map to all walls
    this.tiles = Array(this.map_height).fill(0).map((_, y) => 
      Array(this.map_width).fill(0).map((_, x) => new Tile(x, y, 0))
    );
    
    this.entities = [];
    this.items = [];
    this.combat_log = [];
    
    // Generate rooms
    const rooms: Room[] = [];
    const max_rooms = 20 + this.dungeon_level * 2;
    const min_room_size = 8;
    const max_room_size = 20;
    let player_placed = false;
    
    for (let i = 0; i < max_rooms; i++) {
      const w = Math.floor(Math.random() * (max_room_size - min_room_size + 1)) + min_room_size;
      const h = Math.floor(Math.random() * (max_room_size - min_room_size + 1)) + min_room_size;
      const x = Math.floor(Math.random() * (this.map_width - w - 1)) + 1;
      const y = Math.floor(Math.random() * (this.map_height - h - 1)) + 1;
      const new_room = new Room(x, y, w, h);
      
      let failed = false;
      for (const other_room of rooms) {
        if (new_room.intersects(other_room)) {
          failed = true;
          break;
        }
      }
      
      if (!failed) {
        this.carve_room(new_room);
        const [new_x, new_y] = new_room.center;
        
        if (!player_placed) {
          this.player.x = new_x;
          this.player.y = new_y;
          player_placed = true;
        } else {
          const prev_center = rooms[rooms.length - 1].center;
          if (Math.random() < 0.5) {
            this.carve_h_tunnel(prev_center[0], new_x, prev_center[1]);
            this.carve_v_tunnel(prev_center[1], new_y, new_x);
          } else {
            this.carve_v_tunnel(prev_center[1], new_y, prev_center[0]);
            this.carve_h_tunnel(prev_center[0], new_x, new_y);
          }
        }
        
        this.place_entities(new_room);
        rooms.push(new_room);
      }
    }
    
    if (!player_placed) {
      this.player.x = 1;
      this.player.y = 1;
      this.tiles[1][1].type = 1;
    }
    
    if (rooms.length > 0) {
      const last_room = rooms[rooms.length - 1];
      this.exit_pos = last_room.center;
      this.tiles[this.exit_pos[1]][this.exit_pos[0]].type = 2;
    }
    
    this.update_fov();
  }

  colorDistance(color1: string, color2: string): number {
    // Convert hex to RGB
    const r1 = parseInt(color1.substring(1, 3), 16);
    const g1 = parseInt(color1.substring(3, 5), 16);
    const b1 = parseInt(color1.substring(5, 7), 16);
    
    const r2 = parseInt(color2.substring(1, 3), 16);
    const g2 = parseInt(color2.substring(3, 5), 16);
    const b2 = parseInt(color2.substring(5, 7), 16);
    
    return Math.abs(r1 - r2) + Math.abs(g1 - g2) + Math.abs(b1 - b2);
  }

  carve_room(room: Room) {
    for (let y = room.y + 1; y < room.y + room.h; y++) {
      for (let x = room.x + 1; x < room.x + room.w; x++) {
        this.tiles[y][x].type = 1;
      }
    }
  }

  carve_h_tunnel(x1: number, x2: number, y: number) {
    for (let x = Math.min(x1, x2); x <= Math.max(x1, x2); x++) {
      this.tiles[y][x].type = 1;
    }
  }

  carve_v_tunnel(y1: number, y2: number, x: number) {
    for (let y = Math.min(y1, y2); y <= Math.max(y1, y2); y++) {
      this.tiles[y][x].type = 1;
    }
  }

  place_entities(room: Room) {
    // Place enemies
    const num_enemies = Math.floor(Math.random() * (2 + Math.floor(this.dungeon_level / 2)));
    const enemy_types = ["goblin", "orc",  "zombie", "troll", "ghost"];
    const enemy_chars = ["g", "o", "z", "T", "G"];
    const enemy_colors = [GREEN, GREEN, WHITE, GREEN, PURPLE];
    
    for (let i = 0; i < num_enemies; i++) {
      let placed = false;
      let attempts = 0;
      
      while (!placed && attempts < 10) {
        const x = Math.floor(Math.random() * (room.w - 2)) + room.x + 1;
        const y = Math.floor(Math.random() * (room.h - 2)) + room.y + 1;
        
        if (this.tiles[y][x].type === 1 && 
            !this.entities.some(e => e.x === x && e.y === y) &&
            (x !== this.player.x || y !== this.player.y) &&
            (x !== this.exit_pos[0] || y !== this.exit_pos[1])) {
          
          const enemy_type = Math.floor(Math.random() * enemy_types.length);
          let hp = 15 + (this.dungeon_level - 1) * 3;
          let attack = 5 + (this.dungeon_level - 1);
          let defense = 1 + (this.dungeon_level - 1);
          let exp = 10 + (this.dungeon_level - 1) * 2;
          const special: Record<string, any> = {};
          
          switch (enemy_types[enemy_type]) {
            case "goblin":
              hp = Math.floor(Math.random() * 11) + 25 + (this.dungeon_level - 1) * 2;
              attack = Math.floor(Math.random() * 4) + 3 + (this.dungeon_level - 1);
              defense = Math.floor(Math.random() * 3) + (this.dungeon_level - 1);
              exp = Math.floor(Math.random() * 8) + 8 + (this.dungeon_level - 1);
              special.double_attack_chance = 0.2; // 20% chance to attack twice
              break;
              
            case "orc":
              hp = Math.floor(Math.random() * 11) + 35 + (this.dungeon_level - 1) * 5;
              attack = Math.floor(Math.random() * 5) + 6 + (this.dungeon_level - 1) * 2 + 5;
              defense = Math.floor(Math.random() * 3) + 2 + (this.dungeon_level - 1);
              exp = Math.floor(Math.random() * 16) + 15 + (this.dungeon_level - 1) * 2;
              special.crit_chance = 0.15; // 15% chance to crit
              special.crit_multiplier = 1.5; // 1.5x damage on crit
              break;
              
            case "skeleton":
              hp = Math.floor(Math.random() * 6) + 20 + (this.dungeon_level - 1) * 2;
              attack = Math.floor(Math.random() * 4) + 5 + (this.dungeon_level - 1);
              defense = Math.floor(Math.random() * 3) + 1 + (this.dungeon_level - 1);
              exp = Math.floor(Math.random() * 11) + 10 + (this.dungeon_level - 1);
              special.accuracy_bonus = 2; // +2 to hit
              special.dodge_chance = 0.15; // 15% chance to dodge
              break;
              
            case "zombie":
              hp = Math.floor(Math.random() * 11) + 30 + (this.dungeon_level - 1) * 6;
              attack = Math.floor(Math.random() * 4) + 4 + (this.dungeon_level - 1);
              defense = Math.floor(Math.random() * 3) + 3 + (this.dungeon_level - 1);
              exp = Math.floor(Math.random() * 11) + 12 + (this.dungeon_level - 1);
              special.poison_chance = 0.3; // 30% chance to poison
              special.poison_damage = 2; // 2 damage per turn
              special.poison_duration = 3; // lasts 3 turns
              break;
              
            case "troll":
              hp = Math.floor(Math.random() * 16) + 35 + (this.dungeon_level - 1) * 8;
              attack = Math.floor(Math.random() * 6) + 7 + (this.dungeon_level - 1) * 2;
              defense = Math.floor(Math.random() * 3) + 2 + (this.dungeon_level - 1);
              exp = Math.floor(Math.random() * 16) + 25 + (this.dungeon_level - 1) * 3;
              special.regeneration = true; // Can regenerate health
              special.regen_amount = 3 + this.dungeon_level; // 3-5 HP per turn
              break;
              
            case "ghost":
              hp = Math.floor(Math.random() * 11) + 20 + (this.dungeon_level - 1) * 3;
              attack = Math.floor(Math.random() * 5) + 5 + (this.dungeon_level - 1);
              defense = Math.floor(Math.random() * 3) + 4 + (this.dungeon_level - 1) + 2;
              exp = Math.floor(Math.random() * 16) + 20 + (this.dungeon_level - 1) * 2;
              special.dodge_chance = 0.25; // 25% chance to dodge
              special.life_drain = 0.25; // 25% of damage heals the ghost
              break;
          }
          
          const enemy = new Entity(
            x, y, 
            enemy_chars[enemy_type], 
            enemy_colors[enemy_type], 
            enemy_types[enemy_type], 
            hp, attack, defense, exp
          );
          
          if (Object.keys(special).length > 0) {
            enemy.special = special;
          }
          
          this.entities.push(enemy);
          placed = true;
        }
        attempts++;
      }
    }
    
    // Place items
    if (Math.random() < 1) { // 100% chance for items
      let placed = false;
      let attempts = 0;
      
      while (!placed && attempts < 10) {
        const x = Math.floor(Math.random() * (room.w - 2)) + room.x + 1;
        const y = Math.floor(Math.random() * (room.h - 2)) + room.y + 1;
        
        if (this.tiles[y][x].type === 1 && 
            !this.entities.some(e => e.x === x && e.y === y) &&
            !this.items.some(i => i.x === x && i.y === y) &&
            (x !== this.player.x || y !== this.player.y) &&
            (x !== this.exit_pos[0] || y !== this.exit_pos[1])) {
          
          const item_type = ["health", "weapon", "armor", "treasure", "gold"][
            Math.floor(Math.random() * 5)
          ];
          
          let item;
          if (item_type === "gold") {
            const amount = Math.floor(Math.random() * 11) + 5 + this.dungeon_level;
            item = new Entity(x, y, "$", GOLD, "Gold");
            (item as any).effect = "gold";
            (item as any).amount = amount;
          } else if (item_type === "treasure") {
            const amount = Math.floor(Math.random() * 76) + 25 + (this.dungeon_level * 10);
            item = new Entity(x, y, "T", YELLOW, "Treasure");
            (item as any).effect = "gold";
            (item as any).amount = amount;
          } else if (item_type === "health") {
            const amount = Math.floor(Math.random() * 16) + 10 + (this.dungeon_level - 1) * 5;
            item = new Entity(x, y, "H", RED, "Health Potion");
            (item as any).effect = "heal";
            (item as any).amount = amount;
          } else if (item_type === "weapon") {
            const amount = Math.floor(Math.random() * 3) + 1 + (this.dungeon_level - 1);
            item = new Entity(x, y, "W", BLUE, "Weapon");
            (item as any).effect = "attack";
            (item as any).amount = amount;
          } else { // armor
            const amount = Math.floor(Math.random() * 2) + 1 + (this.dungeon_level - 1);
            item = new Entity(x, y, "A", BLUE, "Armor");
            (item as any).effect = "defense";
            (item as any).amount = amount;
          }
          
          this.items.push(item);
          placed = true;
        }
        attempts++;
      }
    }
  }

  update_fov() {
    // Reset visibility
    for (let y = 0; y < this.map_height; y++) {
      for (let x = 0; x < this.map_width; x++) {
        this.tiles[y][x].visible = false;
      }
    }
    
    // Mark player's tile as visible
    this.tiles[this.player.y][this.player.x].visible = true;
    this.tiles[this.player.y][this.player.x].explored = true;
    
    // Cast light in all directions
    const radius = this.player.vision_radius;
    for (let octant = 0; octant < 8; octant++) {
      this.cast_light(this.player.x, this.player.y, 1, 1.0, 0.0, radius, octant);
    }
  }

  cast_light(cx: number, cy: number, row: number, start_slope: number, end_slope: number, radius: number, octant: number) {
    if (start_slope < end_slope) return;
    
    const radius_squared = radius * radius;
    let new_start_slope = start_slope; // Initialize here
    
    for (let j = row; j <= radius; j++) {
      let dx = -j - 1;
      let dy = -j;
      let blocked = false;
      
      while (dx <= 0) {
        dx += 1;
        
        const [nx, ny] = this.transform_octant(cx, cy, dx, dy, octant);
        if (nx < 0 || ny < 0 || nx >= this.map_width || ny >= this.map_height) continue;
        
        const l_slope = (dx - 0.5) / (dy + 0.5);
        const r_slope = (dx + 0.5) / (dy - 0.5);
        
        if (start_slope < r_slope) continue;
        if (end_slope > l_slope) break;
        
        if (dx*dx + dy*dy < radius_squared) {
          this.tiles[ny][nx].visible = true;
          this.tiles[ny][nx].explored = true;
        }
        
        if (blocked) {
          if (this.tiles[ny][nx].type === 0) { // Wall
            new_start_slope = r_slope;
            continue;
          } else {
            blocked = false;
            start_slope = new_start_slope;
          }
        } else {
          if (this.tiles[ny][nx].type === 0 && j < radius) { // Wall
            blocked = true;
            this.cast_light(cx, cy, j + 1, start_slope, l_slope, radius, octant);
            new_start_slope = r_slope;
          }
        }
      }
      
      if (blocked) break;
    }
  }

  transform_octant(cx: number, cy: number, dx: number, dy: number, octant: number): [number, number] {
    switch (octant) {
      case 0: return [cx + dx, cy + dy];
      case 1: return [cx + dy, cy + dx];
      case 2: return [cx - dy, cy + dx];
      case 3: return [cx - dx, cy + dy];
      case 4: return [cx - dx, cy - dy];
      case 5: return [cx - dy, cy - dx];
      case 6: return [cx + dy, cy - dx];
      case 7: return [cx + dx, cy - dy];
      default: return [cx + dx, cy + dy];
    }
  }

  update_camera() {
    this.camera_x = this.player.x * GRID_SIZE - SCREEN_WIDTH / 2;
    this.camera_y = this.player.y * GRID_SIZE - SCREEN_HEIGHT / 2;
    
    const max_x = this.map_width * GRID_SIZE - SCREEN_WIDTH;
    const max_y = this.map_height * GRID_SIZE - SCREEN_HEIGHT;
    this.camera_x = Math.max(0, Math.min(this.camera_x, max_x));
    this.camera_y = Math.max(0, Math.min(this.camera_y, max_y));
  }

  move_player(dx: number, dy: number) {
    const new_x = this.player.x + dx;
    const new_y = this.player.y + dy;
    
    if (new_x < 0 || new_y < 0 || new_x >= this.map_width || new_y >= this.map_height) {
      this.add_message("You can't go that way!");
      return;
    }
    
    // Update facing direction
    if (dx > 0) {
      this.player.last_direction = "right";
    } else if (dx < 0) {
      this.player.last_direction = "left";
    }
    
    if (this.tiles[new_y][new_x].type === 0) {
      this.add_message("You can't walk through walls!");
      return;
    }
    
    if (new_x === this.exit_pos[0] && new_y === this.exit_pos[1]) {
      this.dungeon_level += 1;
      this.add_message(`Descending to dungeon level ${this.dungeon_level}...`);
      this.generate_dungeon();
      this.update_fov();
      return;
    }
    
    // Check entities
    for (const entity of [...this.entities]) {
      if (entity.x === new_x && entity.y === new_y) {
        this.fight(entity);
        return;
      }
    }
    
    // Check items
    for (const item of [...this.items]) {
      if (item.x === new_x && item.y === new_y) {
        this.pick_up_item(item);
        this.player.x = new_x;
        this.player.y = new_y;
        this.update_fov();
        return;
      }
    }
    
    this.player.x = new_x;
    this.player.y = new_y;
    this.update_fov();
    this.move_enemies();
  }

  fight(entity: Entity) {
    // Player attacks first
    const player_damage = this.calculate_damage(this.player, entity);
    entity.show_health = true;
    entity.health_bar_time = Date.now();
    
    if (entity.special?.dodge_chance && Math.random() < entity.special.dodge_chance) {
      this.add_message(`${entity.name} dodged your attack!`);
      this.add_to_log(`${entity.name} dodged your attack!`);
    } else {
      entity.hp -= player_damage;
      this.add_message(`You hit ${entity.name} for ${player_damage} damage!`);
      this.add_to_log(`You hit ${entity.name} for ${player_damage} damage!`);
    }
    
    this.handle_special_effects(this.player, entity);
    
    if (entity.hp <= 0) {
      entity.alive = false;
      this.entities = this.entities.filter(e => e !== entity);
      this.player.exp += entity.exp;
      this.add_message(`You defeated ${entity.name}! Gained ${entity.exp} XP.`);
      this.add_to_log(`You defeated ${entity.name}! Gained ${entity.exp} XP.`);
      
      if (this.player.exp >= this.player.next_level) {
        const level_up_msg = this.player.level_up();
        this.add_message(level_up_msg);
        this.add_to_log(level_up_msg);
      }
      
      this.player.x = entity.x;
      this.player.y = entity.y;
      this.update_fov();
    } else if (entity.alive) {
      // Enemy attacks
      const player_dodge_chance = 0.1 + (this.player.level * 0.01);
      if (Math.random() < player_dodge_chance) {
        this.add_message(`You dodged ${entity.name}'s attack!`);
        this.add_to_log(`You dodged ${entity.name}'s attack!`);
      } else {
        let attack_again = true;
        while (attack_again) {
          const enemy_damage = this.calculate_damage(entity, this.player);
          this.player.hp -= enemy_damage;
          this.add_message(`${entity.name} hits you for ${enemy_damage} damage!`);
          this.add_to_log(`${entity.name} hits you for ${enemy_damage} damage!`);
          
          attack_again = this.handle_special_effects(entity, this.player);
          
          if (this.player.hp <= 0) {
            this.player.hp = 0;
            this.game_state = "game_over";
            this.add_message("You have been defeated! Refresh to restart");
            return true;
          }
        }
      }
    }
    
    return false;
  }

  calculate_damage(attacker: Entity, defender: Entity): number {
    const effective_defense = Math.max(0, defender.defense - (attacker as any).armor_pen || 0);
    const attack_bonus = attacker.special?.accuracy_bonus || 0;
    let base_attack = Math.max(1, attacker.attack + attack_bonus - Math.floor(effective_defense / 2));
    
    // Critical hits
    const crit_chance = attacker === this.player ? this.player.crit_chance : attacker.special?.crit_chance || 0;
    const crit_multiplier = attacker === this.player ? this.player.crit_multiplier : attacker.special?.crit_multiplier || 1;
    
    if (Math.random() < crit_chance) {
      base_attack = Math.floor(base_attack * crit_multiplier);
      if (attacker === this.player) {
        this.add_message("Critical hit!");
        this.add_to_log("Critical hit!");
      } else {
        this.add_message(`${attacker.name} lands a critical hit!`);
        this.add_to_log(`${attacker.name} lands a critical hit!`);
      }
    }
    
    // Random variance
    const damage = Math.max(1, base_attack + Math.floor(Math.random() * 4) - 1);
    
    // Life drain
    if (attacker.special?.life_drain && attacker !== this.player && damage > 0) {
      const heal_amount = Math.floor(damage * attacker.special.life_drain);
      attacker.hp = Math.min(attacker.max_hp, attacker.hp + heal_amount);
      this.add_message(`${attacker.name} drains ${heal_amount} health!`);
      this.add_to_log(`${attacker.name} drains ${heal_amount} health!`);
    }
    
    return damage;
  }

  handle_special_effects(attacker: Entity, defender: Entity): boolean {
    // Poison effects
    if (attacker.special?.poison_chance && 
        Math.random() < attacker.special.poison_chance &&
        defender === this.player) {
      this.player.poisoned = true;
      this.player.poison_damage = attacker.special.poison_damage || 1;
      this.player.poison_duration = attacker.special.poison_duration || 2;
      this.add_message(`You were poisoned by ${attacker.name}!`);
      this.add_to_log(`Poisoned! ${this.player.poison_damage} damage per turn for ${this.player.poison_duration} turns`);
    }
    
    // Regeneration
    if (attacker.special?.regeneration && attacker !== this.player && attacker.alive) {
      const regen_amount = attacker.special.regen_amount || 1;
      attacker.hp = Math.min(attacker.max_hp, attacker.hp + regen_amount);
      this.add_message(`${attacker.name} regenerates ${regen_amount} HP!`);
      this.add_to_log(`${attacker.name} regenerates ${regen_amount} HP`);
    }
    
    // Double attack for goblins
    if (attacker.special?.double_attack_chance && 
        Math.random() < attacker.special.double_attack_chance &&
        attacker !== this.player) {
      this.add_message(`${attacker.name} attacks twice!`);
      this.add_to_log(`${attacker.name} attacks twice!`);
      return true;
    }
    
    return false;
  }

  handle_poison() {
    if (this.player.poisoned && this.player.poison_duration > 0) {
      this.player.hp -= this.player.poison_damage;
      this.player.poison_duration -= 1;
      this.add_message(`You take ${this.player.poison_damage} poison damage! (${this.player.poison_duration} turns remaining)`);
      this.add_to_log(`Poison damage: -${this.player.poison_damage} HP`);
      
      if (this.player.poison_duration <= 0) {
        this.player.poisoned = false;
        this.add_message("The poison has worn off!");
        this.add_to_log("Poison effect ended");
      }
      
      if (this.player.hp <= 0) {
        this.player.hp = 0;
        this.game_state = "game_over";
        this.add_message("You have been defeated by poison! Refresh to restart");
      }
    }
  }

  pick_up_item(item: Entity) {
    this.items = this.items.filter(i => i !== item);
    
    const effect = (item as any).effect;
    const amount = (item as any).amount;
    
    if (effect === "gold") {
      this.player.gold += amount;
      if (item.name === "Treasure") {
        this.add_message(`You found a treasure chest with ${amount} gold! (Total: ${this.player.gold})`);
        this.add_to_log(`Found treasure worth ${amount} gold`);
      } else {
        this.add_message(`You picked up ${amount} gold! (Total: ${this.player.gold})`);
        this.add_to_log(`Picked up ${amount} gold`);
      }
    } else if (effect === "heal") {
      const heal_amount = Math.min(amount, this.player.max_hp - this.player.hp);
      this.player.hp += heal_amount;
      this.add_message(`You used ${item.name} and healed ${heal_amount} HP!`);
      this.add_to_log(`You used ${item.name} and healed ${heal_amount} HP!`);
    } else if (effect === "attack") {
      this.player.attack += amount;
      this.add_message(`You equipped ${item.name}! Attack + ${amount}.`);
      this.add_to_log(`You equipped ${item.name}! Attack + ${amount}.`);
    } else if (effect === "defense") {
      this.player.defense += amount;
      this.player.max_hp += amount * 2;
      this.player.hp += amount * 2;
      this.add_message(`You equipped ${item.name}! Defense + ${amount} and max HP + ${amount * 2}.`);
      this.add_to_log(`You equipped ${item.name}! Defense + ${amount} and max HP + ${amount * 2}.`);
    }
  }

  add_message(msg: string) {
    this.message = msg;
    this.message_time = Date.now();
  }

  add_to_log(entry: string) {
    this.combat_log.push(entry);
    if (this.combat_log.length > this.max_log_entries) {
      this.combat_log = this.combat_log.slice(-this.max_log_entries);
    }
  }

  move_enemies() {
    for (const entity of this.entities) {
      if (!entity.alive) continue;
      
      const dx = entity.x - this.player.x;
      const dy = entity.y - this.player.y;
      const distance = Math.max(Math.abs(dx), Math.abs(dy));
      
      if (distance <= 5) {
        entity.show_health = true;
        entity.health_bar_time = Date.now();
      }
      
      if (distance > this.enemy_agro_range) continue;
      
      let move_x = 0;
      let move_y = 0;
      if (dx > 0) move_x = -1;
      else if (dx < 0) move_x = 1;
      
      if (dy > 0) move_y = -1;
      else if (dy < 0) move_y = 1;
      
      if (move_x !== 0 && move_y !== 0) {
        if (Math.random() < 0.5) move_y = 0;
        else move_x = 0;
      }
      
      const new_x = entity.x + move_x;
      const new_y = entity.y + move_y;
      
      if (new_x >= 0 && new_x < this.map_width && 
          new_y >= 0 && new_y < this.map_height && 
          this.tiles[new_y][new_x].type !== 0) {
        
        let occupied = false;
        for (const other of this.entities) {
          if (other !== entity && other.x === new_x && other.y === new_y && other.alive) {
            occupied = true;
            break;
          }
        }
        
        if (new_x === this.player.x && new_y === this.player.y) {
          this.fight(entity);
          continue;
        }
        
        if (!occupied) {
          entity.x = new_x;
          entity.y = new_y;
        }
      }
    }
  }

  draw(ctx: CanvasRenderingContext2D) {
    // Clear screen
    ctx.fillStyle = BLACK;
    ctx.fillRect(0, 0, SCREEN_WIDTH, SCREEN_HEIGHT);
    
    // Calculate visible area
    const start_x = Math.max(0, Math.floor(this.camera_x / GRID_SIZE));
    const start_y = Math.max(0, Math.floor(this.camera_y / GRID_SIZE));
    const end_x = Math.min(this.map_width, Math.ceil((this.camera_x + SCREEN_WIDTH) / GRID_SIZE) + 1);
    const end_y = Math.min(this.map_height, Math.ceil((this.camera_y + SCREEN_HEIGHT) / GRID_SIZE) + 1);
    
    // Draw map
    for (let y = start_y; y < end_y; y++) {
      for (let x = start_x; x < end_x; x++) {
        const screen_x = x * GRID_SIZE - this.camera_x;
        const screen_y = y * GRID_SIZE - this.camera_y;
        const tile = this.tiles[y][x];
        
        if (tile.visible) {
          if (tile.type === 0) { // Wall
            ctx.fillStyle = this.wall_color;
            ctx.fillRect(screen_x, screen_y, GRID_SIZE, GRID_SIZE);
          } else if (tile.type === 1) { // Floor
            ctx.fillStyle = this.floor_color;
            ctx.fillRect(screen_x, screen_y, GRID_SIZE, GRID_SIZE);
          } else if (tile.type === 2) { // Exit
            ctx.fillStyle = YELLOW;
            ctx.fillRect(screen_x, screen_y, GRID_SIZE, GRID_SIZE);
            ctx.fillStyle = BLACK;
            ctx.font = `${GRID_SIZE}px Arial`;
            ctx.fillText("E", screen_x + GRID_SIZE / 4, screen_y + GRID_SIZE - GRID_SIZE / 4);
          }
        } else if (tile.explored) {
          if (tile.type === 0) { // Wall
            ctx.fillStyle = this.darkenColor(this.wall_color);
            ctx.fillRect(screen_x, screen_y, GRID_SIZE, GRID_SIZE);
          } else if (tile.type === 1 || tile.type === 2) { // Floor or Exit
            ctx.fillStyle = this.darkenColor(this.floor_color);
            ctx.fillRect(screen_x, screen_y, GRID_SIZE, GRID_SIZE);
          }
        }
      }
    }
    
    // Draw entities and items
    for (const entity of this.entities) {
      if (this.tiles[entity.y][entity.x].visible) {
        const screen_x = entity.x * GRID_SIZE - this.camera_x;
        const screen_y = entity.y * GRID_SIZE - this.camera_y;
        entity.draw(ctx, screen_x, screen_y, GRID_SIZE);
      }
    }
    
    for (const item of this.items) {
      if (this.tiles[item.y][item.x].visible) {
        const screen_x = item.x * GRID_SIZE - this.camera_x;
        const screen_y = item.y * GRID_SIZE - this.camera_y;
        item.draw(ctx, screen_x, screen_y, GRID_SIZE);
      }
    }
    
    // Draw player
    const player_x = this.player.x * GRID_SIZE - this.camera_x;
    const player_y = this.player.y * GRID_SIZE - this.camera_y;
    this.player.draw(ctx, player_x, player_y, GRID_SIZE);
    
    // Draw UI
    this.draw_ui(ctx);
    
    // Draw message
    if (Date.now() - this.message_time < 3000) {
      ctx.fillStyle = WHITE;
      ctx.font = "20px Arial";
      ctx.fillText(this.message, 10, SCREEN_HEIGHT - 40);
    }
    
    // Draw game over screen
    if (this.game_state === "game_over") {
      this.draw_game_over(ctx);
    }
    
    // Draw minimap
    if (this.game_state === "playing") {
      this.draw_minimap(ctx);
    }
  }

  darkenColor(color: string): string {
    // Simple function to darken a hex color
    const num = parseInt(color.substring(1), 16);
    const r = Math.floor((num >> 16) / 2);
    const g = Math.floor(((num >> 8) & 0x00FF) / 2);
    const b = Math.floor((num & 0x0000FF) / 2);
    return `#${((r << 16) | (g << 8) | b).toString(16).padStart(6, '0')}`;
  }

  draw_ui(ctx: CanvasRenderingContext2D) {
    // Draw semi-transparent UI panel
    ctx.fillStyle = 'rgba(0, 0, 0, 0.8)';
    ctx.fillRect(0, SCREEN_HEIGHT - 120, SCREEN_WIDTH, 120);
    
    // Draw health bar
    const health_ratio = this.player.hp / this.player.max_hp;
    const bar_width = 400;
    const bar_height = 24;
    const health_bar_x = SCREEN_WIDTH / 2 - bar_width / 2;
    const health_bar_y = SCREEN_HEIGHT - 80;
    
    // Health bar background
    ctx.fillStyle = DARK_GRAY;
    ctx.fillRect(health_bar_x, health_bar_y, bar_width, bar_height);
    
    // Health bar foreground
    let health_color;
    if (health_ratio > 0.6) health_color = GREEN;
    else if (health_ratio > 0.3) health_color = YELLOW;
    else health_color = RED;
    
    ctx.fillStyle = health_color;
    ctx.fillRect(health_bar_x + 2, health_bar_y + 2, bar_width * health_ratio - 4, bar_height - 4);
    
    // Health text
    ctx.fillStyle = WHITE;
    ctx.font = "16px Arial";
    const health_text = `HP: ${this.player.hp}/${this.player.max_hp}`;
    const health_text_x = SCREEN_WIDTH / 2 - ctx.measureText(health_text).width / 2;
    ctx.fillText(health_text, health_text_x, health_bar_y - 10);
    
    // Level and XP
    ctx.fillText(`Level: ${this.player.level}`, health_bar_x, health_bar_y + bar_height + 20);
    const xp_text = `XP: ${this.player.exp}/${this.player.next_level}`;
    ctx.fillText(xp_text, health_bar_x + bar_width - ctx.measureText(xp_text).width, health_bar_y + bar_height + 20);
    
    // Stats columns
    const columns = [
      [
        `Attack: ${this.player.attack}`,
        `Defense: ${this.player.defense}`
      ],
      [
        `Crit: ${Math.floor(this.player.crit_chance * 100)}%`,
        `Vision: ${this.player.vision_radius}`
      ],
      [
        `Gold: ${this.player.gold}`,
        `Dungeon: ${this.dungeon_level}`
      ]
    ];
    
    let start_x = health_bar_x + bar_width + 100;
    const column_spacing = 120;
    
    for (let i = 0; i < columns.length; i++) {
      const column_x = start_x + i * column_spacing;
      for (let j = 0; j < columns[i].length; j++) {
        ctx.fillText(columns[i][j], column_x, SCREEN_HEIGHT - 100 + j * 22);
      }
    }
    
    // Combat log
    ctx.fillStyle = 'rgba(0, 0, 0, 0.6)';
    ctx.fillRect(10, 10, 300, 230);
    
    ctx.fillStyle = WHITE;
    ctx.fillText("Combat Log:", 20, 30);
    
    let y_offset = 50;
    const lines_to_show = Math.min(10, this.combat_log.length);
    const start_index = Math.max(0, this.combat_log.length - lines_to_show);
    
    for (let i = start_index; i < this.combat_log.length; i++) {
      ctx.fillText(this.combat_log[i], 20, y_offset);
      y_offset += 20;
    }
    
    // Controls
    ctx.fillText("WASD: Move     R: Restart", SCREEN_WIDTH - 300, SCREEN_HEIGHT - 30);
  }

  draw_game_over(ctx: CanvasRenderingContext2D) {
    ctx.fillStyle = 'rgba(0, 0, 0, 0.8)';
    ctx.fillRect(0, 0, SCREEN_WIDTH, SCREEN_HEIGHT);
    
    ctx.fillStyle = RED;
    ctx.font = "48px Arial";
    const game_over_text = "GAME OVER";
    ctx.fillText(game_over_text, SCREEN_WIDTH / 2 - ctx.measureText(game_over_text).width / 2, SCREEN_HEIGHT / 2 - 50);
    
    ctx.fillStyle = WHITE;
    ctx.font = "24px Arial";
    const restart_text = "Refresh the page to restart";
    ctx.fillText(restart_text, SCREEN_WIDTH / 2 - ctx.measureText(restart_text).width / 2, SCREEN_HEIGHT / 2 + 20);
  }

  draw_minimap(ctx: CanvasRenderingContext2D) {
    // Create minimap
    ctx.fillStyle = 'rgba(0, 0, 0, 0.6)';
    ctx.fillRect(MINIMAP_POSITION.x, MINIMAP_POSITION.y, MINIMAP_WIDTH, MINIMAP_HEIGHT);
    
    // Calculate visible area on minimap
    const start_x = Math.max(0, this.player.x - Math.floor(MINIMAP_WIDTH / (2 * MINIMAP_CELL_SIZE)));
    const start_y = Math.max(0, this.player.y - Math.floor(MINIMAP_HEIGHT / (2 * MINIMAP_CELL_SIZE)));
    const end_x = Math.min(this.map_width, start_x + Math.floor(MINIMAP_WIDTH / MINIMAP_CELL_SIZE));
    const end_y = Math.min(this.map_height, start_y + Math.floor(MINIMAP_HEIGHT / MINIMAP_CELL_SIZE));
    
    // Draw explored tiles
    for (let y = start_y; y < end_y; y++) {
      for (let x = start_x; x < end_x; x++) {
        if (this.tiles[y][x].explored) {
          const map_x = MINIMAP_POSITION.x + (x - start_x) * MINIMAP_CELL_SIZE;
          const map_y = MINIMAP_POSITION.y + (y - start_y) * MINIMAP_CELL_SIZE;
          
          let color;
          if (this.tiles[y][x].type === 0) color = this.wall_color;
          else if (this.tiles[y][x].type === 1) color = this.floor_color;
          else color = YELLOW;
          
          if (!this.tiles[y][x].visible) {
            color = this.darkenColor(color);
          }
          
          ctx.fillStyle = color;
          ctx.fillRect(map_x, map_y, MINIMAP_CELL_SIZE, MINIMAP_CELL_SIZE);
        }
      }
    }
    
    // Draw player position
    const player_map_x = MINIMAP_POSITION.x + (this.player.x - start_x) * MINIMAP_CELL_SIZE;
    const player_map_y = MINIMAP_POSITION.y + (this.player.y - start_y) * MINIMAP_CELL_SIZE;
    ctx.fillStyle = GREEN;
    ctx.fillRect(player_map_x, player_map_y, MINIMAP_CELL_SIZE, MINIMAP_CELL_SIZE);
    
    // Draw border
    ctx.strokeStyle = WHITE;
    ctx.lineWidth = 1;
    ctx.strokeRect(MINIMAP_POSITION.x, MINIMAP_POSITION.y, MINIMAP_WIDTH, MINIMAP_HEIGHT);
  }
}

// React component for the game
export default function DungeonCrawler() {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [gameStarted, setGameStarted] = useState(false);
  const gameRef = useRef<Game | null>(null);
  const animationRef = useRef<number | null>(null);
  const lastUpdateRef = useRef<number>(0);
  const keysRef = useRef<Set<string>>(new Set());

  useEffect(() => {
    if (!gameStarted) return;

    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Initialize game
    gameRef.current = new Game();
    const game = gameRef.current;

    // Handle keyboard input
    const handleKeyDown = (e: KeyboardEvent) => {
      keysRef.current.add(e.key.toLowerCase());
    };

    const handleKeyUp = (e: KeyboardEvent) => {
      keysRef.current.delete(e.key.toLowerCase());
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    // Game loop
    const gameLoop = (timestamp: number) => {
      const game = gameRef.current;
      if (!game) return;

      // Handle movement
      const keys = keysRef.current;
      let dx = 0;
      let dy = 0;

      if (keys.has('w') || keys.has('arrowup')) dy = -1;
      if (keys.has('s') || keys.has('arrowdown')) dy = 1;
      if (keys.has('a') || keys.has('arrowleft')) dx = -1;
      if (keys.has('d') || keys.has('arrowright')) dx = 1;

      if (dx !== 0 || dy !== 0) {
        game.move_player(dx, dy);
        // Clear the movement keys after processing - CORRECTED VERSION
        if (dy === -1) {
          keys.delete('w');
          keys.delete('arrowup');
        }
        if (dy === 1) {
          keys.delete('s');
          keys.delete('arrowdown');
        }
        if (dx === -1) {
          keys.delete('a');
          keys.delete('arrowleft');
        }
        if (dx === 1) {
          keys.delete('d');
          keys.delete('arrowright');
        }
      }

      // Handle poison
      if (game.player.poisoned) {
        game.handle_poison();
      }

      // Update camera and draw
      game.update_camera();
      game.draw(ctx);

      animationRef.current = requestAnimationFrame(gameLoop);
    };

    animationRef.current = requestAnimationFrame(gameLoop);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [gameStarted]);

  const startGame = () => {
    setGameStarted(true);
  };

  if (!gameStarted) {
    return (
      <div className="flex flex-col items-center justify-center h-screen bg-gray-900 text-white">
        <h1 className="text-4xl font-bold mb-6">Dungeon Crawler</h1>
        <p className="mb-8 text-lg max-w-md text-center">
          A procedurally generated rogue-like dungeon crawler. Explore, fight monsters, and find treasure!
        </p>
        <button
          onClick={startGame}
          className="px-6 py-3 bg-green-600 hover:bg-green-700 rounded-lg font-bold text-lg transition-colors"
        >
          Start Game
        </button>
        <div className="mt-8 text-gray-400">
          <h2 className="text-xl font-semibold mb-2">Controls:</h2>
          <ul className="list-disc pl-5">
            <li>WASD or Arrow Keys - Move</li>
            <li>Walk into enemies to attack</li>
            <li>Walk into items to pick them up</li>
          </ul>
        </div>
      </div>
    );
  }

  return (
    <div className="flex justify-center items-center h-screen bg-black">
      <canvas
        ref={canvasRef}
        width={SCREEN_WIDTH}
        height={SCREEN_HEIGHT}
        className="border border-gray-800"
      />
    </div>
  );
}